mapper.select:
SELECT

from prm_log l
left join tb_user u on l.member_id = u.user_id
where 1=1

    and project_id = #{projectId}


    and log_flag = #{logFlag}


    and log_type = #{logType}

mapper.delete:
delete from prm_log where public_id in
(

    #{publicId}

)

mapper.select:
select

from prm_log l join tb_user u on l.member_id=u.user_id








    AND l.create_time >= #{startTime}




    AND l.create_time <= #{endTime}


   and l.member_id=#{memberId}

where l.project_id in(

    #{projectId}
)
