mapper.select:
select

from prm_organization_member where organization_id = #{condition.organizationId}

	and create_time BETWEEN (#{condition.createTime} - 60 * 1000 * 60 * 24 * 7) and #{condition.createTime}



	AND partment_id = #{condition.partmentId}



	AND member_lock = #{condition.memberLock}


	AND other = #{condition.other}

order by create_time asc

SELECT
from prm_organization_member where 1=1
AND organization_id = #{organizationId}

mapper.update:
update prm_organization_member
set organization_lable = 0
WHERE organization_id = #{orgId} and member_id = #{ownerId}

update prm_organization_member
set organization_lable = 1
WHERE organization_id = #{orgId} and member_id = #{memberId}
